mapper.select:
select 

from app_patient_manual_weight
where 
    fk_patient_id = #{fkPatientId}

    and fk_tenant_id = #{fkTenantId,jdbcType=INTEGER}


    and record_date = #{recordDate,jdbcType=TIMESTAMP}


    and DATE_FORMAT(record_date,'%Y-%m-%d') = #{recordDateStr}


    and DATE_FORMAT(record_date,'%Y-%m-%d') >= #{startRecordDateStr}


    and DATE_FORMAT(record_date,'%Y-%m-%d') <= #{endRecordDateStr}


    and weight = #{weight,jdbcType=DECIMAL}


    and create_time = #{createTime,jdbcType=TIMESTAMP}


    and create_user_id = #{createUserId,jdbcType=BIGINT}


    and update_time = #{updateTime,jdbcType=TIMESTAMP}


    and update_user_id = #{updateUserId,jdbcType=BIGINT}

  order by record_date

SELECT * from
(select date_format(c1.record_date,'%Y-%m-%d') as recordDateStr,c1.weight,c1.fk_patient_id
    from app_patient_manual_weight c1 
    where c1.fk_patient_id=#{fkPatientId} 

        and DATE_FORMAT(c1.record_date,'%Y-%m-%d')>=#{startDateStr}


        and DATE_FORMAT(c1.record_date,'%Y-%m-%d') <=#{endDateStr}

    order by c1.record_date desc
    ) a1
    GROUP BY recordDateStr

SELECT  date_format(a1.record_date,'%Y-%m-%d') as recordDateStr,a1.weight,a1.fk_patient_id
    from app_patient_manual_weight a1 
    where a1.fk_patient_id=#{fkPatientId} 

        and DATE_FORMAT(a1.record_date,'%Y-%m-%d')>=#{startDateStr}

    GROUP BY a1.fk_patient_id,date_format(a1.record_date,'%Y-%m-%d') 
    limit 1
