mapper.select:
select 

from app_hospital_address


        and name like concat('%', #{name}, '%')


        and url = #{url,jdbcType=VARCHAR}


        and group_flag = #{groupFlag,jdbcType=BIT}


        and is_enable = #{isEnable,jdbcType=BIT}


        and check_connect = #{checkConnect,jdbcType=BIT}


        and pull_and_push = #{pullAndPush,jdbcType=VARCHAR}


order by spell_initials,order_by
