mapper.select:
SELECT count('1') 
from assay_hosp_dict

     
        and item_code = #{itemCode,jdbcType=VARCHAR}
    
    
        and fk_tenant_id = #{tenantId,jdbcType=INTEGER}

      SELECT dhl.*
        ,pad.item_name AS fkItemName 
      FROM (SELECT  from assay_hosp_dict ahd join assay_hosp_dict_group_mapping ahdgm on ahd.item_code = ahdgm.fk_item_code and ahd.fk_tenant_id = ahdgm.fk_tenant_id
join assay_hosp_dict_group ahdg  on ahdg.group_id = ahdgm.fk_group_id and ahdg.fk_tenant_id = ahdgm.fk_tenant_id) dhl
      LEFT JOIN patient_assay_dictionary pad ON pad.item_code = dhl.fk_dict_code
     
      
        and dhl.group_id = #{groupId}
      
      
        and dhl.group_name = #{groupName}
      
      
        and dhl.item_code = #{itemCode}
      
      
        and dhl.item_name = #{itemName}
      
      
        and dhl.min_value = #{minValue}
      
      
        and dhl.max_value = #{maxValue}
      
      
        and dhl.unit = #{unit}
      
      
        and dhl.reference = #{reference}
      
      
        and dhl.order_by = #{orderBy}
      
      
        and dhl.fk_dict_code = #{fkDictCode}
      
      
        and dhl.fk_tenant_id = #{fkTenantId}
      
      
        and dhl.create_time = #{createTime}
      
      
        and dhl.create_user_id = #{createUserId}
      
      
        and dhl.update_time = #{updateTime}
      
      
        and dhl.update_user_id = #{updateUserId}
      
      
        and dhl.is_top = #{isTop}
      
      
        and dhl.value_type = #{valueType,jdbcType=INTEGER}
      
      
        and dhl.fk_dict_code in #{item}
      
    
    ORDER BY dhl.group_name ASC,dhl.item_code ASC

                   SELECT
                
            FROM
                assay_hosp_dict ahd join assay_hosp_dict_group_mapping ahdgm on ahd.item_code = ahdgm.fk_item_code and ahd.fk_tenant_id = ahdgm.fk_tenant_id
join assay_hosp_dict_group ahdg  on ahdg.group_id = ahdgm.fk_group_id and ahdg.fk_tenant_id = ahdgm.fk_tenant_id 
            WHERE
                ahd.fk_tenant_id = #{fkTenantId}
            AND ahdg.group_id = #{groupId}
            AND ahd.item_code= #{itemCode}

      SELECT ahdg.group_id,ahdg.group_name FROM assay_hosp_dict ahd join assay_hosp_dict_group_mapping ahdgm on ahd.item_code = ahdgm.fk_item_code and ahd.fk_tenant_id = ahdgm.fk_tenant_id
join assay_hosp_dict_group ahdg  on ahdg.group_id = ahdgm.fk_group_id and ahdg.fk_tenant_id = ahdgm.fk_tenant_id 
      WHERE ahd.fk_tenant_id = #{fkTenantId} 
      
        and ahd.value_type = #{valueType,jdbcType=INTEGER}
      
      GROUP BY ahdg.group_id,ahdg.group_name
      ORDER BY ahdg.group_name ASC

     SELECT ahd.id,ahdg.group_id,ahdg.group_name,ahd.unit,ahd.item_code,ahd.item_name,ahd.reference
      FROM assay_hosp_dict ahd join assay_hosp_dict_group_mapping ahdgm on ahd.item_code = ahdgm.fk_item_code and ahd.fk_tenant_id = ahdgm.fk_tenant_id
join assay_hosp_dict_group ahdg  on ahdg.group_id = ahdgm.fk_group_id and ahdg.fk_tenant_id = ahdgm.fk_tenant_id 
     
      
      and  ahdg.group_id = #{groupId,jdbcType=VARCHAR}
      
      
      and  ahdg.group_name = #{groupName,jdbcType=VARCHAR}
      
      
       and  ahd.item_code = #{itemCode,jdbcType=VARCHAR}
      
      
       and  ahd.item_name = #{itemName,jdbcType=VARCHAR}
      
      
      and  ahd.min_value = #{minValue,jdbcType=DOUBLE}
      
      
      and  ahd.max_value = #{maxValue,jdbcType=DOUBLE}
      
      
      and  ahd.value_type = #{valueType,jdbcType=INTEGER}
      
      
      and  ahd.reference = #{reference,jdbcType=VARCHAR}
      
      
      and  ahd.order_by = #{orderBy,jdbcType=INTEGER}
      
      
      and  ahd.fk_dict_code = #{fkDictCode,jdbcType=VARCHAR}
      
      
      and  ahd.fk_tenant_id = #{fkTenantId,jdbcType=INTEGER}
      
      
      and  ahd.unit = #{unit,jdbcType=VARCHAR}
      
      
      and  ahd.create_time = #{createTime,jdbcType=TIMESTAMP}
      
      
      and  ahd.create_user_id = #{createUserId,jdbcType=BIGINT}
      
      
      and  ahd.update_time = #{updateTime,jdbcType=TIMESTAMP}
      
      
      and  ahd.update_user_id = #{updateUserId,jdbcType=BIGINT}
      
      
      and  ahd.date_type = #{dateType,jdbcType=VARCHAR}
      
      
      and  ahd.personal_min_value = #{personalMinValue,jdbcType=DECIMAL}
      
      
      and  ahd.personal_max_value = #{personalMaxValue,jdbcType=DECIMAL}
      
      
      and  ahd.is_top = #{isTop,jdbcType=BIT}
      
      
      and  ahd.method = #{method,jdbcType=LONGVARCHAR}

        SELECT id from assay_hosp_dict ahd join assay_hosp_dict_group_mapping ahdgm on ahd.item_code = ahdgm.fk_item_code and ahd.fk_tenant_id = ahdgm.fk_tenant_id
join assay_hosp_dict_group ahdg  on ahdg.group_id = ahdgm.fk_group_id and ahdg.fk_tenant_id = ahdgm.fk_tenant_id 
        
            
                and ahd.item_name = #{itemName,jdbcType=VARCHAR}
            
            
                and ahdg.group_id = #{groupId,jdbcType=VARCHAR}

        SELECT
        count(id)
        FROM
        (
        SELECT ahd.id FROM assay_hosp_dict ahd join assay_hosp_dict_group_mapping ahdgm on ahd.item_code = ahdgm.fk_item_code and ahd.fk_tenant_id = ahdgm.fk_tenant_id
join assay_hosp_dict_group ahdg  on ahdg.group_id = ahdgm.fk_group_id and ahdg.fk_tenant_id = ahdgm.fk_tenant_id
        
            
                AND ahdg.group_id = #{groupId,jdbcType=VARCHAR}
            
            
              and  ahd.fk_tenant_id = #{fkTenantId,jdbcType=INTEGER}
            
            AND ahd.flage IS NULL OR ahd.flage = 0
        
        GROUP BY ahdg.group_name
        ) AS temp;

SELECT
count(id)
FROM
(
SELECT id FROM assay_hosp_dict

    
        and item_code = #{itemCode,jdbcType=VARCHAR}
    
    
        and id != #{id,jdbcType=BIGINT}
    
    
       and  fk_tenant_id = #{fkTenantId,jdbcType=INTEGER}
    

) AS temp
