mapper.select:
SELECT count('1') 
from assay_hosp_dict

     
        and item_code = #{itemCode,jdbcType=VARCHAR}
    
    
        and fk_tenant_id = #{tenantId,jdbcType=INTEGER}

  SELECT 
    
    ,pad.item_name AS fkItemName 
  FROM assay_hosp_dict ahd 
  JOIN assay_hosp_dict_group_mapping ahdgm ON ahd.item_code = ahdgm.fk_item_code and ahd.fk_tenant_id = ahdgm.fk_tenant_id
  JOIN assay_hosp_dict_group ahdg ON ahdg.group_id = ahdgm.fk_group_id and ahdg.fk_tenant_id = ahdgm.fk_tenant_id
  LEFT JOIN patient_assay_dictionary pad ON pad.item_code = ahd.item_code
 
  
    and ahdg.group_id = #{groupId}
  
  
    and ahdg.group_name = #{groupName}
  
  
    and ahd.item_code = #{itemCode}
  
  
    and ahd.item_name = #{itemName}
  
  
    and ahd.min_value = #{minValue}
  
  
    and ahd.max_value = #{maxValue}
  
  
    and ahd.unit = #{unit}
  
  
    and ahd.reference = #{reference}
  
  
    and ahd.order_by = #{orderBy}
  
  
    and ahd.fk_dict_code = #{fkDictCode}
  
  
    and ahd.fk_tenant_id = #{fkTenantId}
  
  
    and ahd.is_top = #{isTop}
  
  
    and ahd.value_type = #{valueType,jdbcType=INTEGER}
  
  
    and ahd.is_auto = #{isAuto}
  
  
    and ahd.fk_dict_code in #{item}
  

ORDER BY ahdg.group_name ASC,ahd.item_code ASC

SELECT

FROM assay_hosp_dict ahd 
JOIN assay_hosp_dict_group_mapping ahdgm on ahd.item_code = ahdgm.fk_item_code and ahd.fk_tenant_id = ahdgm.fk_tenant_id
JOIN assay_hosp_dict_group ahdg on ahdg.group_id = ahdgm.fk_group_id and ahdg.fk_tenant_id = ahdgm.fk_tenant_id
WHERE ahd.fk_tenant_id = #{fkTenantId}
AND ahdg.group_id = #{groupId}
AND ahd.item_code= #{itemCode}

SELECT ahdg.group_id,ahdg.group_name FROM assay_hosp_dict ahd JOIN assay_hosp_dict_group_mapping ahdgm on ahd.item_code = ahdgm.fk_item_code and ahd.fk_tenant_id = ahdgm.fk_tenant_id JOIN assay_hosp_dict_group ahdg on ahdg.group_id = ahdgm.fk_group_id and ahdg.fk_tenant_id = ahdgm.fk_tenant_id WHERE ahd.fk_tenant_id = #{fkTenantId}
and ahd.value_type = #{valueType,jdbcType=INTEGER}
GROUP BY ahdg.group_id,ahdg.group_name ORDER BY ahdg.group_name ASC

select is_top, id,date_type, personal_min_value, personal_max_value from assay_hosp_dict 
where item_code = #{itemCode,jdbcType=VARCHAR}
   and fk_tenant_id = #{fkTenantId}

SELECT ahdg.group_id, ahdg.group_name , max(IF(pac.id is null, 0, 1)) as selectFlag, 1 categoryFlag FROM assay_hosp_dict_group_mapping ahdgm JOIN assay_hosp_dict_group ahdg on ahdg.group_id = ahdgm.fk_group_id and ahdg.fk_tenant_id = ahdgm.fk_tenant_id LEFT JOIN patient_assay_class pac ON pac.item_code = ahdgm.fk_item_code and pac.fk_tenant_id = ahdgm.fk_tenant_id
AND pac.assay_class = #{assayClass}
where ahdgm.fk_tenant_id = #{fkTenantId} GROUP BY ahdg.group_id ORDER BY ahdg.group_name ASC

SELECT ahd.item_code, ahd.item_name , ahdg.group_id, ahdg.group_name , IF(pac.id is null, 0, 1) as selectFlag, 0 categoryFlag FROM assay_hosp_dict ahd JOIN assay_hosp_dict_group_mapping ahdgm on ahd.item_code = ahdgm.fk_item_code and ahd.fk_tenant_id = ahdgm.fk_tenant_id JOIN assay_hosp_dict_group ahdg on ahdg.group_id = ahdgm.fk_group_id and ahdg.fk_tenant_id = ahdgm.fk_tenant_id LEFT JOIN patient_assay_class pac ON pac.item_code = ahd.item_code and pac.fk_tenant_id = ahd.fk_tenant_id
AND pac.assay_class = #{assayClass}
WHERE ahd.fk_tenant_id = #{fkTenantId} ORDER BY ahdg.group_id, ahd.item_name

SELECT ahd.item_code, ahd.item_name , ahdg.group_id, ahdg.group_name , IF(agcd.item_code is null, 0, 1) as selectFlag FROM assay_hosp_dict ahd JOIN assay_hosp_dict_group_mapping ahdgm on ahd.item_code = ahdgm.fk_item_code and ahd.fk_tenant_id = ahdgm.fk_tenant_id JOIN assay_hosp_dict_group ahdg on ahdg.group_id = ahdgm.fk_group_id and ahdg.fk_tenant_id = ahdgm.fk_tenant_id LEFT JOIN assay_group_conf_detail agcd ON ahdg.item_code = agcd.item_code AND agcd.fk_tenant_id = ahd.fk_tenant_id AND agcd.fk_assay_group_conf_id = #{fkAssayGroupConfId} WHERE ahd.fk_tenant_id = #{fkTenantId} AND NOT EXISTS ( SELECT 1 FROM assay_group_conf_detail WHERE fk_tenant_id = ahd.fk_tenant_id AND item_code = ahd.item_code
and fk_assay_group_conf_id != #{fkAssayGroupConfId}
) ORDER BY selectFlag desc, ahdg.group_id, ahd.item_name

SELECT ahd.id from assay_hosp_dict ahd
JOIN assay_hosp_dict_group_mapping ahdgm ON ahd.item_code = ahdgm.fk_item_code and ahd.fk_tenant_id = ahdgm.fk_tenant_id
JOIN assay_hosp_dict_group ahdg ON ahdg.group_id = ahdgm.fk_group_id and ahdg.fk_tenant_id = ahdgm.fk_tenant_id

    
        and ahd.item_name = #{itemName,jdbcType=VARCHAR}
    
    
        and ahd.fk_tenant_id = #{fkTenantId}
    
    
        and ahdg.group_id = #{groupId,jdbcType=VARCHAR}

SELECT count(ahdg.group_name)
FROM assay_hosp_dict_group ahdg

    
        AND ahdg.group_id = #{groupId,jdbcType=VARCHAR}
    
    
        and ahdg.fk_tenant_id = #{fkTenantId,jdbcType=INTEGER}
    

GROUP BY ahdg.group_name

SELECT count(id) FROM assay_hosp_dict

    
        and item_code = #{itemCode,jdbcType=VARCHAR}
    
    
        and id != #{id,jdbcType=BIGINT}
    
    
        and fk_tenant_id = #{fkTenantId,jdbcType=INTEGER}

    SELECT  
            ahd.id
            ,ahd.item_code
            ,ahd.item_name
            ,ahd.reference
            ,ahd.unit
            ,ahd.fk_dict_code
            ,pad.item_name AS fkItemName
    FROM assay_hosp_dict ahd 
    LEFT JOIN patient_assay_dictionary pad ON pad.item_code = ahd.fk_dict_code

    
    and ahd.group_id = #{groupId}
    
    
    and ahd.group_name = #{groupName}
    
    
    and ahd.item_code = #{itemCode}
    
    
    and ahd.item_name = #{itemName}
    
    
    and ahd.min_value = #{minValue}
    
    
    and ahd.max_value = #{maxValue}
    
    
    and ahd.unit = #{unit}
    
    
    and ahd.reference = #{reference}
    
    
    and ahd.order_by = #{orderBy}
    
    
    and ahd.fk_dict_code = #{fkDictCode}
    
    
    and ahd.fk_tenant_id = #{fkTenantId}
    
    
    and ahd.create_time = #{createTime}
    
    
    and ahd.create_user_id = #{createUserId}
    
    
    and ahd.update_time = #{updateTime}
    
    
    and ahd.update_user_id = #{updateUserId}
    
    
    and ahd.is_top = #{isTop}
    
    
    and ahd.value_type = #{valueType,jdbcType=INTEGER}
    
    
    and ahd.fk_dict_code in #{item}
    

ORDER BY ahd.item_code ASC
