mapper.select:
select
e.id as e_id, e.fk_patient_id as e_fk_patient_id, e.item_code as e_item_code, e.fk_tenant_id as e_fk_tenant_id, e.create_time as e_create_time, e.create_user_id as e_create_user_id, e.update_time as e_update_time, e.update_user_id as e_update_user_id,
v.id as v_id, v.fk_patient_id as v_fk_patient_id, v.fk_entity_id as v_fk_entity_id, v.item_code as v_item_code, v.item_value as v_item_value, v.fk_tenant_id as v_fk_tenant_id, v.create_time as v_create_time, v.create_user_id as v_create_user_id, v.update_time as v_update_time, v.update_user_id as v_update_user_id from cm_diagnosis_entity e,cm_diagnosis_entity_value v where v.fk_entity_id=e.id and v.id=#{valueId}
