mapper.insert:

insert into cm_diagnosis_hist_surgery (id, fk_patient_id, surgery_date, 
  surgery_name, fk_tenant_id, create_time, 
  create_user_id, update_time, update_user_id, remark
  )
values (#{id,jdbcType=BIGINT}, #{fkPatientId,jdbcType=BIGINT}, #{surgeryDate,jdbcType=DATE}, 
  #{surgeryName,jdbcType=VARCHAR}, #{fkTenantId,jdbcType=BIGINT}, #{createTime,jdbcType=TIMESTAMP}, 
  #{createUserId,jdbcType=BIGINT}, #{updateTime,jdbcType=TIMESTAMP}, #{updateUserId,jdbcType=BIGINT},
  #{remark,jdbcType=LONGVARCHAR}
  )

mapper.select:
select cdhs.*,st.name, (case when usu.name is not null and usu.name != '' then usu.name else su.name end) as operatorName from cm_diagnosis_hist_surgery cdhs INNER JOIN sys_user su on su.id = cdhs.create_user_id INNER JOIN sys_user usu on usu.id = cdhs.update_user_id INNER JOIN sys_tenant st on st.id = cdhs.fk_tenant_id where cdhs.fk_patient_id = #{patientId,jdbcType=BIGINT}
and cdhs.fk_tenant_id in (SELECT fk_tenant_id from sys_group_tenant where fk_group_id = (SELECT fk_group_id from sys_group_tenant where fk_tenant_id = ${multiTenant }))
order by cdhs.id desc
