mapper.select:
  	SELECT fbi.* from cm_form_base_items fbi
  	
	  
        fbi.p_item_code = #{pItemCode}
      
      
        and fbi.item_code = #{itemCode}
      
      
        and fbi.item_name = #{itemName}
      
      
        and fbi.item_type = #{itemType}
      
      
        and fbi.unit = #{unit}
      
      
        and fbi.need_score = #{needScore}
      
      
        and fbi.sys_owner = #{sysOwner}
      
      
        and fbi.score = #{score}
      
      
        and fbi.is_leaf = #{isLeaf}
      
      
        and fbi.is_fixed = #{isFixed}
      
      
        and fbi.fk_code = #{fkCode}
      
      
        and fbi.data_type = #{dataType}
      
      
        and fbi.is_enable = #{isEnable}
      
      
        and fbi.fk_tenant_id = #{fkTenantId}
      
  	
  	ORDER BY fbi.p_item_code,fbi.item_code
