mapper.insert:

insert into cm_form_items (id, fk_form_id, item_name, 
  p_item_code, item_code, order_by, 
  sys_owner, item_path, item_level, 
  is_leaf, is_enable, fk_tenant_id
  )
values (#{id,jdbcType=BIGINT}, #{fkFormId,jdbcType=BIGINT}, #{itemName,jdbcType=VARCHAR}, 
  #{pItemCode,jdbcType=VARCHAR}, #{itemCode,jdbcType=VARCHAR}, #{orderBy,jdbcType=INTEGER}, 
  #{sysOwner,jdbcType=VARCHAR}, #{itemPath,jdbcType=VARCHAR}, #{itemLevel,jdbcType=INTEGER}, 
  #{isLeaf,jdbcType=BIT}, #{isEnable,jdbcType=BIT}, #{fkTenantId,jdbcType=INTEGER}
  )

mapper.select:
 	SELECT fi.*
 		,f.is_new AS isNew,f.category AS category
	FROM cm_form_items fi
	join cm_form f on fi.fk_form_id = f.id
 	
	 
        fi.fk_form_id = #{fkFormId}
      
      
        and fi.item_name = #{itemName}
      
      
        and fi.p_item_code = #{pItemCode}
      
      
        and fi.item_code = #{itemCode}
      
      
        and fi.order_by = #{orderBy}
      
      
        and fi.sys_owner = #{sysOwner}
      
      
        and fi.item_path = #{itemPath}
      
      
        and fi.item_level = #{itemLevel}
      
      
        and fi.is_leaf = #{isLeaf}
      
      
        and fi.is_enable = #{isEnable}
      
      
        and fi.fk_tenant_id = #{fkTenantId}
      
      
        AND f.is_new = #{isNew}
      
      
        AND f.sys_owner = #{sysOwner}
      
      
        AND f.category = #{category}
      
	
	ORDER BY fi.p_item_code,fi.order_by
