mapper.select:
	 select f.* from cm_form f
  
    
      f.form_name = #{formName}
    
    
      and f.category = #{category}
    
    
      and f.sys_owner = #{sysOwner}
    
    
      and f.is_new = #{isNew}
    
    
      and f.version = #{version}
    
    
      and f.fk_tenant_id = #{fkTenantId}
