mapper.select:
  	SELECT 
		fi.fk_form_id,fi.item_name,fi.p_item_code,fi.item_code,fi.order_by,fi.sys_owner,fi.item_path,fi.item_level,fi.is_leaf,fi.is_enable,fi.fk_tenant_id
		,fbi.item_type,fbi.item_desc,fbi.unit,fbi.need_score,fbi.score,fbi.is_fixed,fbi.fk_code,fbi.data_type,fbi.min_value,fbi.max_value,fbi.display_cols,fbi.group_tag
	FROM cm_form_items fi 
	LEFT JOIN cm_form_base_items fbi ON fbi.item_code = fi.item_code AND fbi.fk_tenant_id = fi.fk_tenant_id and fbi.sys_owner = fi.sys_owner
  	
  		
  		
  		  fi.fk_form_id=#{fkFormId}
  		
  		
		  AND fi.item_name = #{itemName}
		
		
		  AND fi.p_item_code = #{pItemCode}
		
		
		  AND fi.item_code = #{itemCode}
		
		
		  AND fi.order_by = #{orderBy}
		
		
		  AND fi.sys_owner = #{sysOwner}
		
		
		  AND fi.item_path = #{itemPath}
		
		
		  AND fi.item_level = #{itemLevel}
		
		
		  AND fi.is_leaf = #{isLeaf}
		
		
		  AND fi.is_enable = #{isEnable}
		
		
		  AND fi.fk_tenant_id = #{fkTenantId}
		
		
		
		  AND fbi.item_type = #{itemType}
		
		
		  AND fbi.unit = #{unit}
		
		
		  AND fbi.is_fixed = #{isFixed}
		
		
		  AND fbi.fk_code = #{fkCode}
		
		
		  AND fbi.data_type = #{dataType}
		
  	
  	ORDER BY fi.p_item_code,fi.order_by
