mapper.select:
	select fv.* 
	from cm_form_value fv
	
	  
      fv.fk_record_id = #{fkRecordId}
    
    
    	AND fv.fk_form_id = #{fkFormId}
    
    
      AND fv.item_code = #{itemCode}
    
    
      AND fv.item_value = #{itemValue}
    
    
      AND fv.fk_tenant_id = #{fkTenantId}
    
    
      AND fv.sys_owner = #{sysOwner}
    
	
	ORDER BY fv.item_code
