mapper.insert:

insert into cm_patient_diagnosis_value (id, fk_patient_diagnosis_id, fk_patient_id, 
  item_code, item_value, fk_tenant_id
  )
values (#{id,jdbcType=BIGINT}, #{fkPatientDiagnosisId,jdbcType=BIGINT}, #{fkPatientId,jdbcType=BIGINT}, 
  #{itemCode,jdbcType=VARCHAR}, #{itemValue,jdbcType=VARCHAR}, #{fkTenantId,jdbcType=INTEGER}
  )

mapper.select:
	select *
	from cm_patient_diagnosis_value cpdc
	
    
      AND fk_patient_diagnosis_id = #{fkPatientDiagnosisId,jdbcType=BIGINT}
    
    
      AND fk_patient_id = #{fkPatientId,jdbcType=BIGINT}
    
    
      AND item_code = #{itemCode,jdbcType=VARCHAR}
    
    
      AND item_value = #{itemValue,jdbcType=VARCHAR}
    
    
      AND fk_tenant_id = #{fkTenantId,jdbcType=INTEGER}
