mapper.select:
SELECT des.*,st.name, u.`name` AS docName,u.image_path AS docImagePath,u.id AS docId FROM dialysis_exception_stop des LEFT JOIN sys_tenant st on des.fk_tenant_id = st.id LEFT JOIN sys_user u ON u.id = des.operator_id WHERE des.fk_patient_id = #{patientId}
AND des.fk_tenant_id in (${groupTenant})
AND des.create_time >= #{startDate}
AND des.create_time <= #{endDate}
ORDER BY des.fk_dialysis_campaign_id DESC
