mapper.insert:

insert into dict_grade (id, type_code, type_name, 
  item_name, item_code, score, 
  fk_tenant_id, is_enable, create_time, 
  create_user_id, update_time, update_user_id
  )
values (#{id,jdbcType=BIGINT}, #{typeCode,jdbcType=VARCHAR}, #{typeName,jdbcType=VARCHAR}, 
  #{itemName,jdbcType=VARCHAR}, #{itemCode,jdbcType=VARCHAR}, #{score,jdbcType=INTEGER}, 
  #{fkTenantId,jdbcType=INTEGER}, #{isEnable,jdbcType=BIT}, #{createTime,jdbcType=TIMESTAMP}, 
  #{createUserId,jdbcType=BIGINT}, #{updateTime,jdbcType=TIMESTAMP}, #{updateUserId,jdbcType=BIGINT}
  )

mapper.select:
	SELECT dg.* FROM dict_grade dg


      dg.type_code = #{typeCode}


      and dg.type_code in #{item}


      and dg.type_name = #{typeName}


      and dg.item_name = #{itemName}


      and dg.item_code = #{itemCode}


      and dg.score = #{score}


      and dg.fk_tenant_id = #{fkTenantId}


      and dg.is_enable = #{isEnable}


  ORDER BY dg.type_code,dg.item_code

SELECT dg.* FROM dict_grade dg 
WHERE dg.fk_tenant_id = #{fkTenantId}
GROUP BY dg.type_code ORDER BY dg.type_code
