mapper.select:
  SELECT dhl.*
    ,pad.item_name AS fkItemName 
  FROM dict_hospital_lab dhl 
  LEFT JOIN patient_assay_dictionary pad ON pad.item_code = dhl.fk_dict_code
 
  
    and dhl.group_id = #{groupId}
  
  
    and dhl.group_name = #{groupName}
  
  
    and dhl.item_code = #{itemCode}
  
  
    and dhl.item_name = #{itemName}
  
  
    and dhl.min_value = #{minValue}
  
  
    and dhl.max_value = #{maxValue}
  
  
    and dhl.unit = #{unit}
  
  
    and dhl.reference = #{reference}
  
  
    and dhl.order_by = #{orderBy}
  
  
    and dhl.fk_dict_code = #{fkDictCode}
  
  
    and dhl.fk_tenant_id = #{fkTenantId}
  
  
    and dhl.create_time = #{createTime}
  
  
    and dhl.create_user_id = #{createUserId}
  
  
    and dhl.update_time = #{updateTime}
  
  
    and dhl.update_user_id = #{updateUserId}
  
  
    and dhl.is_top = #{isTop}
  
  
    and dhl.value_type = #{valueType,jdbcType=INTEGER}
  
  
    and dhl.fk_dict_code in #{item}
  

ORDER BY dhl.group_name ASC,dhl.item_code ASC

       SELECT
    dhl.*
FROM
    dict_hospital_lab dhl
WHERE
    dhl.fk_tenant_id = #{fkTenantId}
AND dhl.group_id = #{groupId}
AND dhl.item_code= #{itemCode}

SELECT dhl.group_id,dhl.group_name FROM dict_hospital_lab dhl 
WHERE dhl.fk_tenant_id = #{fkTenantId} 

  and value_type = #{valueType,jdbcType=INTEGER}

GROUP BY dhl.group_id,dhl.group_name
ORDER BY dhl.group_name ASC

SELECT dhl.group_id, dhl.group_name, max(IF(pac.id is null, 0, 1)) as selectFlag, 1 categoryFlag FROM dict_hospital_lab dhl LEFT JOIN patient_assay_class pac ON pac.item_code = dhl.item_code and dhl.fk_tenant_id = pac.fk_tenant_id
AND pac.assay_class = #{assayClass}
where dhl.fk_tenant_id = #{fkTenantId} GROUP BY dhl.group_id ORDER BY dhl.group_name ASC

SELECT dhl.item_code, dhl.item_name, dhl.group_id, dhl.group_name, IF(pac.id is null, 0, 1) as selectFlag, 0 categoryFlag
FROM dict_hospital_lab dhl
LEFT JOIN patient_assay_class pac ON pac.item_code = dhl.item_code and dhl.fk_tenant_id = pac.fk_tenant_id 

    AND pac.assay_class = #{assayClass}

where dhl.fk_tenant_id =  #{fkTenantId}
ORDER BY dhl.group_name, dhl.item_name

 SELECT id,group_id,group_name,unit,item_code,item_name,reference
  FROM dict_hospital_lab 
 
  
  and  group_id = #{groupId,jdbcType=VARCHAR}
  
  
  and  group_name = #{groupName,jdbcType=VARCHAR}
  
  
   and  item_code = #{itemCode,jdbcType=VARCHAR}
  
  
   and   item_name = #{itemName,jdbcType=VARCHAR}
  
  
  and  min_value = #{minValue,jdbcType=DOUBLE}
  
  
  and  max_value = #{maxValue,jdbcType=DOUBLE}
  
  
  and  value_type = #{valueType,jdbcType=INTEGER}
  
  
  and  reference = #{reference,jdbcType=VARCHAR}
  
  
  and  order_by = #{orderBy,jdbcType=INTEGER}
  
  
  and  fk_dict_code = #{fkDictCode,jdbcType=VARCHAR}
  
  
  and  fk_tenant_id = #{fkTenantId,jdbcType=INTEGER}
  
  
  and  unit = #{unit,jdbcType=VARCHAR}
  
  
  and  create_time = #{createTime,jdbcType=TIMESTAMP}
  
  
  and  create_user_id = #{createUserId,jdbcType=BIGINT}
  
  
  and  update_time = #{updateTime,jdbcType=TIMESTAMP}
  
  
  and  update_user_id = #{updateUserId,jdbcType=BIGINT}
  
  
  and  date_type = #{dateType,jdbcType=VARCHAR}
  
  
  and  personal_min_value = #{personalMinValue,jdbcType=DECIMAL}
  
  
  and  personal_max_value = #{personalMaxValue,jdbcType=DECIMAL}
  
  
  and  is_top = #{isTop,jdbcType=BIT}
  
  
  and  method = #{method,jdbcType=LONGVARCHAR}

SELECT id from dict_hospital_lab 

    
        and item_name = #{itemName,jdbcType=VARCHAR}
    
    
        and group_id = #{groupId,jdbcType=VARCHAR}
    
    
        AND fk_tenant_id=#{fkTenantId}

SELECT
count(id)
FROM
(
SELECT id FROM dict_hospital_lab

    
        AND group_id = #{groupId,jdbcType=VARCHAR}
    
    
      and  fk_tenant_id = #{fkTenantId,jdbcType=INTEGER}
    
    AND flage IS NULL OR flage = 0

GROUP BY group_name
) AS temp;

SELECT
count(id)
FROM
(
SELECT id FROM dict_hospital_lab

    
        and item_code = #{itemCode,jdbcType=VARCHAR}
    
    
        and id != #{id,jdbcType=BIGINT}
    
    
       and  fk_tenant_id = #{fkTenantId,jdbcType=INTEGER}
    

) AS temp
