mapper.select:
      select pad.* from patient_assay_dictionary pad
      
          
	        pad.group_id = #{group_id}
	      
	      
	        and pad.group_name = #{groupName}
	      
	      
	        and pad.item_code =#{itemCode}
	      
	      
	        and pad.item_name =#{itemName}
	      
	      
	        and pad.value_type = #{valueType}
	      
	      
	        and pad.min_value = #{minValue}
	      
	      
	        and pad.max_value = #{maxValue}
	      
	      
	        and pad.other_value = #{otherValue}
	      
	      
	        and pad.unit = #{unit}
	      
	      
	        and pad.order_by = #{orderBy}
	      
	      
	        and pad.is_enable = #{isEnable}
