mapper.select:
      SELECT par.* FROM patient_assay_result par 
      
          
	        fk_patient_id = #{fkPatientId}
	      
	      
	        and par.hav = #{hav}
	      
	      
	        and par.hbv = #{hbv}
	      
	      
	        and par.hcv = #{hcv}
	      
	      
	        and par.hev = #{hev}
	      
	      
	        and par.hiv = #{hiv}
	      
	      
	        and par.hsv = #{hsv}
	      
	      
	        and par.operator_id = #{operatorId},
	      
	      
	        and par.is_enabled = #{isEnabled}
	      
	      
	        and par.fk_tenant_id = #{fkTenantId}
