mapper.select:
select 

from patient_card
where del_flag=1 

       and card_no = #{cardNo,jdbcType=VARCHAR}


       and fk_pt_id = #{fkPtId,jdbcType=BIGINT}

  SELECT pc.* FROM patient_card pc


        pc.fk_pt_id = #{fkPtId}


        AND pc.fk_pt_id in #{item}


        AND pc.card_type = #{cardType}


        AND pc.card_no = #{cardNo}


        AND pc.del_flag = #{delFlag}


        AND pc.new_flag = #{newFlag}


        AND pc.fk_tenant_id = #{fkTenantId}


ORDER BY pc.update_time DESC

 select 
      p.name,
      p.sex,
      p.id_number,
      p.id,
      pc.card_type,
      dct.item_name as card_type_desc,
      pc.card_no,
      pc.create_time,
      pc.new_flag
 from patient_card pc 
 left join patient p on pc.fk_pt_id = p.id
 left join cm_dict dct on pc.card_type = dct.item_code
 where dct.p_item_code = 'medicare_card_type'

      and p.id = #{fkPtId}


      and p.name = #{nameShow}


  AND p.id_number = #{idNumber}


  AND pc.card_type = #{cardType}


  AND pc.card_no = #{cardNo}


  AND pc.del_flag = #{delFlag}


  AND pc.new_flag = #{newFlag}


  AND pc.fk_tenant_id = #{fkTenantId}


  and  pc.create_time >= concat(#{startDate},' 00:00:00')


  and  pc.create_time <= concat(#{endDate},' 23:59:59')

order by  pc.create_time desc

select CONCAT('patient_card_',(@i:=@i+1))as id,(@j:=@j+1) as id_no,'patient_card' as down_type,p.id as fk_patient_id,
   p.name as patient_name,'sfz'as card_type,p.id_number as card_no,
   '1975-01-01' as start_date,DATE_FORMAT(NOW(),'%Y-%m-%d') as end_date,NOW() as create_time,hdpt.fk_tenant_id 
   from patient p left join hd_patient_extend hdpt on p.id = hdpt.id,(select @i:=0) b ,(select @j:=0) c 
   where hdpt.del_flag = 0 and  hdpt.fk_tenant_id = #{fkTenantId} 

       and p.id = #{fkPatientId}
