mapper.select:
	    select 

	    from patient_orders where 1=1 

	    	and  order_no = #{orderNo,jdbcType=VARCHAR}


	    	and  order_id = #{orderId,jdbcType=VARCHAR}


            and enter_time between #{startDate,jdbcType=VARCHAR} and #{endDate,jdbcType=VARCHAR}


            and  po.fk_patient_id = #{fkPatientId,jdbcType=BIGINT}

    	select po.* from patient_orders po
    	inner join dict_orders_item doi on doi.item_code = po.item_code and doi.fk_tenant_id = po.fk_tenant_id


	          po.fk_patient_id = #{fkPatientId,jdbcType=BIGINT}


    		  and po.create_time between concat(#{dateStr}, ' 00:00:00') and concat(#{dateStr}, ' 23:59:59')


    		  and doi.is_download = #{isDownload}


		      and fk_tenant_id = #{fkTenantId,jdbcType=INTEGER}


        	  and del_flag = #{delFlag,jdbcType=BIT}

	   SELECT p.name as ptName,p.sex,po.* 
		   from patient_orders po left join patient p
				ON po.fk_patient_id = p.id
		   WHERE 1=1

	    	and  po.order_no = #{orderNo,jdbcType=VARCHAR}


	    	and  po.order_id = #{orderId,jdbcType=VARCHAR}


	    	and item_name = #{itemName,jdbcType=VARCHAR}


	    	and  p.name = #{ptName,jdbcType=VARCHAR}


	    	and enter_time between #{startDate,jdbcType=VARCHAR} and #{endDate,jdbcType=VARCHAR}


	    	and  po.charges_code = #{chargesCode,jdbcType=VARCHAR}


	    	and  po.fk_patient_id = #{fkPatientId,jdbcType=BIGINT}


            and po.fk_tenant_id = #{fkTenantId,jdbcType=INTEGER}


            and  po.pt_type = #{ptType,jdbcType=BIGINT}


            and  po.order_class = #{orderClass,jdbcType=BIGINT}

       order by po.enter_time desc

 select 

 from patient_orders where 1=1 

     and enter_time between #{startDate,jdbcType=VARCHAR} and #{endDate,jdbcType=VARCHAR}


     and  fk_patient_id = #{fkPatientId,jdbcType=BIGINT}


     and fk_tenant_id = #{fkTenantId,jdbcType=INTEGER}

group by item_name;

SELECT *  FROM  patient_orders   WHERE charges_code = #{chargesCode}
AND fk_patient_id = #{fkPatientId}
AND fk_tenant_id = #{fkTenantId}
