mapper.select:
SELECT po.id, po.fk_patient_id, po.type, po.record_date, po.reason, po.sys_owner, po.fk_tenant_id
,p.name as patientName ,p.serial_num as serialNum
FROM patient_outcome po
JOIN patient p ON p.id = po.fk_patient_id


        po.fk_patient_id = #{fkPatientId}


        AND po.type = #{type}


        AND po.record_date = #{recordDate,jdbcType=DATE}


        AND po.record_date >= #{startDate,jdbcType=DATE}


        AND po.record_date <= #{endDate,jdbcType=DATE}


        AND po.reason = #{reason}


        AND po.sys_owner = #{sysOwner}


        AND po.fk_tenant_id = #{fkTenantId}


        AND po.type not in #{item}


        AND po.type in #{item}


        AND p.is_temp = #{isTemp}


ORDER BY p.initial,p.id,po.record_date desc

SELECT d.item_name `name`, d.item_code value, count(DISTINCT p.id) cnt ,DATE_FORMAT(r.record_date,'%Y-%m') AS time FROM cm_dict d LEFT JOIN patient_outcome r ON d.item_code = r.type AND r.fk_tenant_id = d.fk_tenant_id JOIN patient p ON r.fk_patient_id = p.id AND p.fk_tenant_id = d.fk_tenant_id WHERE r.record_date BETWEEN #{from} and #{to} and d.p_item_code=#{dictType} AND d.is_enable = 1 AND d.fk_tenant_id = #{tenantId}
AND p.is_temp = #{isTemp}
GROUP BY d.item_code,d.item_name,DATE_FORMAT(r.record_date,'%Y-%m')
