mapper.select:
SELECT count(*) value, CASE ${whenAgeRange} END name FROM ( SELECT DATE_FORMAT(NOW(), '%Y') - DATE_FORMAT(birthday, '%Y') - (DATE_FORMAT(NOW(), '00-%m-%d') < DATE_FORMAT(birthday, '00-%m-%d')) AS age FROM patient p WHERE p.fk_tenant_id = #{tenantId} and p.del_flag = 0
AND p.is_temp = #{isTemp}
) t GROUP BY name

select
ROUND(AVG(
DATE_FORMAT(NOW(), '%Y') - DATE_FORMAT(birthday, '%Y') - (DATE_FORMAT(NOW(), '00-%m-%d') < DATE_FORMAT(birthday, '00-%m-%d'))
)) AS avgAge
FROM patient p
WHERE p.fk_tenant_id = #{tenantId}
and p.del_flag = 0

	AND p.is_temp = #{isTemp}

select count(*) value, case sex when 'M' then '男' when 'F' then '女' else '' end name
from (
	select p.sex from
	patient p
	WHERE p.fk_tenant_id = #{tenantId}
	and p.del_flag = 0

		AND p.is_temp = #{isTemp}

) t
group by name
